          <input type='text' value={name } placeholder='Enter Name....' onChange={(e)=>setName(e.target.value)} />
          <input type='text' value={Message} placeholder='Enter Meassge....' onChange={(e)=>setMessage(e.target.value)} />
         <button type='submit' className='btn' >Send GiftðŸ¤ž{amonut ? ` â‚¹ ${amonut}`:`${amonut}`}</button>
        </div>
