import React, { useState } from 'react'
import axios from 'axios'

export default function Contact(){
  const [name,setName]=useState();
  const [email,setEmail]=useState();
  const [Mobile,setMobile]=useState();
  const [text,setText]=useState();

const HandleSend = async(e)=>{
    e.preventDefault();
    // console.log(name,email,Mobile,text);
    // alert(name,email,Mobile)
    
    await axios.post("http://localhost:4000/api/v1/meassge/send",{name,email,Mobile,text},{withCredentials:true,headers:{
      "Content-Type":"application/json"
    }
  }
  ).then(()=>{
    setEmail("")
    setMobile("")
    setName("")
    setText("")
    

  })
  
  return (
    <>
    <section className='contact'>
    <div className='container'>
      <div className='banner'>
   <form onSubmit={HandleSend}>
    <h2>Contact US</h2>
    <input type='text' value={name} placeholder='Enter your name' onChange={(e)=>setName(e.target.value)} className=' '/>
    <input type='email'value={email} placeholder='Enter your Email'  onChange={(e)=>setEmail(e.target.value)} className=' '/>
    <input type='mobile'value={Mobile} placeholder='Enter your Mobile'  onChange={(e)=>setMobile(e.target.value)} className=' '/>
    <textarea rows="10" value={text}  placeholder='Enter your Meassge'  onChange={(e)=>setText(e.target.value)} className=' '/>
    <button type='submit' className='btn'>Send</button>
   </form>
      </div>
    </div>
    </section>
    </>
  )
}
}
